import React, { useState } from "react";
import { Pie } from "react-chartjs-2";

const GHGEmissionsComparison = () => {
  const [formData, setFormData] = useState({
    baselineFuel: "",
    currentFuel: "",
    baselineGas: "",
    currentGas: "",
    baselineElectricity: "",
    currentElectricity: "",
    baselineFlights: "",
    currentFlights: "",
    baselineWaste: "",
    currentWaste: "",
    baselineWater: "",
    currentWater: "",
    baselineWFH: "",
    currentWFH: "",
  });

  const [results, setResults] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const calculateComparison = () => {
    const categories = [
      { key: "Scope 1", baselineKeys: ["baselineFuel", "baselineGas"], currentKeys: ["currentFuel", "currentGas"] },
      { key: "Scope 2", baselineKeys: ["baselineElectricity"], currentKeys: ["currentElectricity"] },
      { key: "Scope 3", baselineKeys: ["baselineFlights", "baselineWaste", "baselineWater", "baselineWFH"], currentKeys: ["currentFlights", "currentWaste", "currentWater", "currentWFH"] },
    ];

    const resultsData = categories.map(({ key, baselineKeys, currentKeys }) => {
      const baselineTotal = baselineKeys.reduce((sum, key) => sum + (parseFloat(formData[key]) || 0), 0);
      const currentTotal = currentKeys.reduce((sum, key) => sum + (parseFloat(formData[key]) || 0), 0);
      const change = currentTotal - baselineTotal;
      const percentChange = baselineTotal !== 0 ? ((change / baselineTotal) * 100).toFixed(2) : "0.00";
      return { category: key, baselineTotal, currentTotal, change, percentChange };
    });

    setResults(resultsData);
  };

  const scopeTotals = results ? results.map((r) => r.currentTotal) : [];
  const scopeBaselineTotals = results ? results.map((r) => r.baselineTotal) : [];
  const scopeChanges = results ? results.map((r) => r.change) : [];

  const totalCurrent = scopeTotals.reduce((sum, val) => sum + val, 0);
  const totalBaseline = scopeBaselineTotals.reduce((sum, val) => sum + val, 0);
  const totalChange = scopeChanges.reduce((sum, val) => sum + val, 0);

  const pieDataTotal = {
    labels: results ? results.map((r) => r.category) : [],
    datasets: [
      {
        data: results ? results.map((r) => ((r.currentTotal / totalCurrent) * 100).toFixed(2)) : [],
        backgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
        hoverBackgroundColor: ["#FF6384", "#36A2EB", "#FFCE56"],
      },
    ],
  };

  const pieDataChange = {
    labels: results ? results.map((r) => r.category) : [],
    datasets: [
      {
        data: results ? results.map((r) => ((Math.abs(r.change) / Math.abs(totalChange)) * 100).toFixed(2)) : [],
        backgroundColor: ["#FF9F40", "#4BC0C0", "#9966FF"],
        hoverBackgroundColor: ["#FF9F40", "#4BC0C0", "#9966FF"],
      },
    ],
  };

  return (
    <div className="container">
      <h1>GHG Emissions Comparison Analysis</h1>
      <form id="ghgCalculator">
        {/* Form Inputs */}
        <h3>Scope 1: Company Vehicles</h3>
        <label>Baseline Year Fuel Consumption (liters):</label>
        <input type="number" id="baselineFuel" placeholder="Enter baseline fuel" value={formData.baselineFuel} onChange={handleChange} />
        <label>Current Year Fuel Consumption (liters):</label>
        <input type="number" id="currentFuel" placeholder="Enter current fuel" value={formData.currentFuel} onChange={handleChange} />
        {/* Add other inputs similarly... */}
        <button type="button" onClick={calculateComparison}>
          Compare Emissions
        </button>
      </form>

      {results && (
        <>
          <div className="result">
            <h3>Comparison Analysis Results</h3>
            {results.map((result) => (
              <div key={result.category}>
                <strong>{result.category}:</strong>
                <ul>
                  <li>Baseline: {result.baselineTotal.toFixed(2)}</li>
                  <li>Current: {result.currentTotal.toFixed(2)}</li>
                  <li>Change: {result.change.toFixed(2)} ({result.percentChange}%)</li>
                </ul>
              </div>
            ))}
          </div>
          <div>
            <h3>Current Emissions Distribution (Percentage)</h3>
            <Pie data={pieDataTotal} />
          </div>
          <div>
            <h3>Emissions Change Distribution (Percentage)</h3>
            <Pie data={pieDataChange} />
          </div>
        </>
      )}
    </div>
  );
};

export default GHGEmissionsComparison;
