import React, { useState } from "react";
import { PieChart, Pie, Cell, Tooltip, Legend } from "recharts";

const GHGEmissionsComparison = () => {
  const [formData, setFormData] = useState({
    baselineFuel: "",
    currentFuel: "",
    baselineGas: "",
    currentGas: "",
    baselineElectricity: "",
    currentElectricity: "",
    baselineFlights: "",
    currentFlights: "",
    baselineWaste: "",
    currentWaste: "",
    baselineWater: "",
    currentWater: "",
    baselineWFH: "",
    currentWFH: "",
  });

  const [results, setResults] = useState(null);
  const [chartData, setChartData] = useState([]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const calculateComparison = () => {
    const categories = [
      { key: "Fuel", baseline: "baselineFuel", current: "currentFuel" },
      { key: "Gas", baseline: "baselineGas", current: "currentGas" },
      { key: "Electricity", baseline: "baselineElectricity", current: "currentElectricity" },
      { key: "Flights", baseline: "baselineFlights", current: "currentFlights" },
      { key: "Waste", baseline: "baselineWaste", current: "currentWaste" },
      { key: "Water", baseline: "baselineWater", current: "currentWater" },
      { key: "Work from Home", baseline: "baselineWFH", current: "currentWFH" },
    ];

    const resultsData = categories.map(({ key, baseline, current }) => {
      const baselineValue = parseFloat(formData[baseline]) || 0;
      const currentValue = parseFloat(formData[current]) || 0;
      const change = currentValue - baselineValue;
      const percentChange = baselineValue !== 0 ? ((change / baselineValue) * 100).toFixed(2) : "0.00";
      return { category: key, baseline: baselineValue, current: currentValue, change, percentChange };
    });

    setResults(resultsData);

    // Calculate totals for Scope 1, Scope 2, and Scope 3
    const scope1 = resultsData
      .filter((item) => item.category === "Fuel" || item.category === "Gas")
      .reduce((sum, item) => sum + item.current, 0);

    const scope2 = resultsData
      .filter((item) => item.category === "Electricity")
      .reduce((sum, item) => sum + item.current, 0);

    const scope3 = resultsData
      .filter(
        (item) =>
          item.category === "Flights" ||
          item.category === "Waste" ||
          item.category === "Water" ||
          item.category === "Work from Home"
      )
      .reduce((sum, item) => sum + item.current, 0);

    setChartData([
      { name: "Scope 1", value: scope1 },
      { name: "Scope 2", value: scope2 },
      { name: "Scope 3", value: scope3 },
    ]);
  };

  const COLORS = ["#0088FE", "#00C49F", "#FFBB28"];

  return (
    <div className="container">
      <h1>GHG Emissions Comparison Analysis</h1>
      <form id="ghgCalculator">
        <h3>Scope 1: Company Vehicles</h3>
        <label>Baseline Year Fuel Consumption (liters):</label>
        <input
          type="number"
          id="baselineFuel"
          placeholder="Enter baseline fuel"
          value={formData.baselineFuel}
          onChange={handleChange}
        />
        <label>Current Year Fuel Consumption (liters):</label>
        <input
          type="number"
          id="currentFuel"
          placeholder="Enter current fuel"
          value={formData.currentFuel}
          onChange={handleChange}
        />

        <h3>Scope 1: Source of Heating (Natural Gas)</h3>
        <label>Baseline Year Gas Usage (m³):</label>
        <input
          type="number"
          id="baselineGas"
          placeholder="Enter baseline gas"
          value={formData.baselineGas}
          onChange={handleChange}
        />
        <label>Current Year Gas Usage (m³):</label>
        <input
          type="number"
          id="currentGas"
          placeholder="Enter current gas"
          value={formData.currentGas}
          onChange={handleChange}
        />

        <h3>Scope 2: Electricity</h3>
        <label>Baseline Year Electricity (kWh):</label>
        <input
          type="number"
          id="baselineElectricity"
          placeholder="Enter baseline electricity"
          value={formData.baselineElectricity}
          onChange={handleChange}
        />
        <label>Current Year Electricity (kWh):</label>
        <input
          type="number"
          id="currentElectricity"
          placeholder="Enter current electricity"
          value={formData.currentElectricity}
          onChange={handleChange}
        />

        <h3>Scope 3: Business Travel</h3>
        <label>Baseline Year Flights (kg CO2e):</label>
        <input
          type="number"
          id="baselineFlights"
          placeholder="Enter baseline flights emissions"
          value={formData.baselineFlights}
          onChange={handleChange}
        />
        <label>Current Year Flights (kg CO2e):</label>
        <input
          type="number"
          id="currentFlights"
          placeholder="Enter current flights emissions"
          value={formData.currentFlights}
          onChange={handleChange}
        />

        <h3>Scope 3: Waste</h3>
        <label>Baseline Year Waste (kg):</label>
        <input
          type="number"
          id="baselineWaste"
          placeholder="Enter baseline waste"
          value={formData.baselineWaste}
          onChange={handleChange}
        />
        <label>Current Year Waste (kg):</label>
        <input
          type="number"
          id="currentWaste"
          placeholder="Enter current waste"
          value={formData.currentWaste}
          onChange={handleChange}
        />

        <h3>Scope 3: Water Consumption</h3>
        <label>Baseline Year Water Usage (liters):</label>
        <input
          type="number"
          id="baselineWater"
          placeholder="Enter baseline water"
          value={formData.baselineWater}
          onChange={handleChange}
        />
        <label>Current Year Water Usage (liters):</label>
        <input
          type="number"
          id="currentWater"
          placeholder="Enter current water"
          value={formData.currentWater}
          onChange={handleChange}
        />

        <h3>Scope 3: Work from Home</h3>
        <label>Baseline Year Work from Home Emissions (kg CO2e):</label>
        <input
          type="number"
          id="baselineWFH"
          placeholder="Enter baseline WFH emissions"
          value={formData.baselineWFH}
          onChange={handleChange}
        />
        <label>Current Year Work from Home Emissions (kg CO2e):</label>
        <input
          type="number"
          id="currentWFH"
          placeholder="Enter current WFH emissions"
          value={formData.currentWFH}
          onChange={handleChange}
        />

        <button type="button" onClick={calculateComparison}>
          Compare Emissions
        </button>
      </form>

      {results && (
        <div className="result">
          <h3>Comparison Analysis Results</h3>
          {results.map((result) => (
            <div key={result.category}>
              <strong>{result.category}:</strong>
              <ul>
                <li>Baseline: {result.baseline.toFixed(2)}</li>
                <li>Current: {result.current.toFixed(2)}</li>
                <li>Change: {result.change.toFixed(2)} ({result.percentChange}%)</li>
              </ul>
            </div>
          ))}
        </div>
      )}

      {chartData.length > 0 && (
        <div className="chart">
          <h3>Scope Emissions Distribution</h3>
          <PieChart width={400} height={400}>
            <Pie
              data={chartData}
              dataKey="value"
              nameKey="name"
              cx="50%"
              cy="50%"
              outerRadius={120}
              fill="#8884d8"
              label
            >
              {chartData.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
              ))}
            </Pie>
            <Tooltip />
            <Legend />
          </PieChart>
        </div>
      )}
    </div>
  );
};

export default GHGEmissionsComparison;
