import React, { useState } from "react";
import { PieChart } from '@mui/x-charts/PieChart';
import InfoBox from "./Infobox";

const GHGEmissionsComparison = () => {
  const [formData, setFormData] = useState({
    baselineFuel: "",
    currentFuel: "",
    baselineGas: "",
    currentGas: "",
    baselineElectricity: "",
    currentElectricity: "",
    baselineFlights: "",
    currentFlights: "",
    baselineWaste: "",
    currentWaste: "",
    baselineWater: "",
    currentWater: "",
    baselineWFH: "",
    currentWFH: "",
  });

  const [results, setResults] = useState(null);
  const [showPieChart, setShowPieChart] = useState(false); // New state for pie chart visibility
  const [pieChartData, setPieChartData] = useState([]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const calculateComparison = () => {
    const categories = [
      { key: "Fuel", baseline: "baselineFuel", current: "currentFuel" },
      { key: "Gas", baseline: "baselineGas", current: "currentGas" },
      { key: "Electricity", baseline: "baselineElectricity", current: "currentElectricity" },
      { key: "Flights", baseline: "baselineFlights", current: "currentFlights" },
      { key: "Waste", baseline: "baselineWaste", current: "currentWaste" },
      { key: "Water", baseline: "baselineWater", current: "currentWater" },
      { key: "Work from Home", baseline: "baselineWFH", current: "currentWFH" },
    ];

    const resultsData = categories.map(({ key, baseline, current }) => {
      const baselineValue = parseFloat(formData[baseline]) || 0;
      const currentValue = parseFloat(formData[current]) || 0;
      const change = currentValue - baselineValue;
      return { category: key, value: change };
    });

    setResults(resultsData);

    // Prepare data for PieChart
    const pieData = resultsData.map((result) => ({
      label: result.category,
      value: Math.abs(result.value), // Ensure values are positive for pie chart visualization
    }));
    setPieChartData(pieData);

    setShowPieChart(true); // Show pie chart after calculation
  };

  return (
    <div className="container">
      <h1>GHG Emissions Comparison Analysis</h1>
      <form id="ghgCalculator">
        <h3>Scope 1: Company Vehicles</h3>
        <label>Baseline Year Fuel Consumption (liters):</label>
        <input type="number" id="baselineFuel" placeholder="Enter baseline fuel" value={formData.baselineFuel} onChange={handleChange} />
        <label>Current Year Fuel Consumption (liters):</label>
        <input type="number" id="currentFuel" placeholder="Enter current fuel" value={formData.currentFuel} onChange={handleChange} />

        {/* Additional inputs here for other categories */}

        <button type="button" onClick={calculateComparison}>
          Compare Emissions
        </button>
      </form>

      {results && (
        <div className="result">
          <h3>Comparison Analysis Results</h3>
          {results.map((result) => (
            <div key={result.category}>
              <strong>{result.category}:</strong> {result.value.toFixed(2)} kg CO2e
            </div>
          ))}
        </div>
      )}

      {showPieChart && (
        <div style={{ marginTop: "20px" }}>
          <h3>Change in GHG Emissions</h3>
          <PieChart
            series={[
              {
                data: pieChartData,
                innerRadius: 30,
                outerRadius: 100,
                paddingAngle: 5,
                cornerRadius: 5,
                startAngle: -45,
                endAngle: 225,
                cx: 150,
                cy: 150,
              },
            ]}
          />
        </div>
      )}

      <div>
        <h2>What are Scopes?</h2>
        <div style={{ display: "flex", justifyContent: "space-around", flexWrap: "wrap" }}>
          <InfoBox
            title="Scope 1"
            description="Direct emissions from the combustion of fuel in assets that a company operates."
          />
          <InfoBox
            title="Scope 2"
            description="Indirect emissions from the generation of purchased energy."
          />
          <InfoBox
            title="Scope 3"
            description="Indirect emissions from sources not owned or directly controlled by the company."
          />
        </div>
      </div>
    </div>
  );
};

export default GHGEmissionsComparison;
