import React, { useState, useRef, useEffect } from "react";
import { Chart as ChartJS, Title, Tooltip, Legend, ArcElement, CategoryScale } from "chart.js";
import { Box, Typography, Grid } from "@mui/material";
import InfoBox from "./Infobox";

ChartJS.register(Title, Tooltip, Legend, ArcElement, CategoryScale);

const PieChart = ({ data, title }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const ctx = chartRef.current.getContext("2d");
      new ChartJS(ctx, {
        type: "pie",
        data,
        options: {
          responsive: true,
          plugins: {
            legend: { position: "top" },
            title: { display: true, text: title },
          },
        },
      });
    }
  }, [data, title]);

  return <canvas ref={chartRef} />;
};

const GHGEmissionsComparison = () => {
  const [formData, setFormData] = useState({
    baselineFuel: "",
    currentFuel: "",
    baselineGas: "",
    currentGas: "",
    baselineElectricity: "",
    currentElectricity: "",
    baselineFlights: "",
    currentFlights: "",
    baselineWaste: "",
    currentWaste: "",
    baselineWater: "",
    currentWater: "",
    baselineWFH: "",
    currentWFH: "",
  });

  const [donutChartData, setDonutChartData] = useState(null);
  const [changeChartData, setChangeChartData] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const calculateComparison = () => {
    const categories = [
      { key: "Fuel", baseline: "baselineFuel", current: "currentFuel" },
      { key: "Gas", baseline: "baselineGas", current: "currentGas" },
      { key: "Electricity", baseline: "baselineElectricity", current: "currentElectricity" },
      { key: "Flights", baseline: "baselineFlights", current: "currentFlights" },
      { key: "Waste", baseline: "baselineWaste", current: "currentWaste" },
      { key: "Water", baseline: "baselineWater", current: "currentWater" },
      { key: "Work from Home", baseline: "baselineWFH", current: "currentWFH" },
    ];

    const resultsData = categories.map(({ key, baseline, current }) => {
      const baselineValue = parseFloat(formData[baseline]) || 0;
      const currentValue = parseFloat(formData[current]) || 0;
      const change = currentValue - baselineValue;
      return { category: key, change };
    });

    const scope1 = resultsData.filter((result) => result.category === "Fuel" || result.category === "Gas")
                              .reduce((sum, result) => sum + result.change, 0);
    const scope2 = resultsData.filter((result) => result.category === "Electricity")
                              .reduce((sum, result) => sum + result.change, 0);
    const scope3 = resultsData.filter((result) =>
      ["Flights", "Waste", "Water", "Work from Home"].includes(result.category)
    ).reduce((sum, result) => sum + result.change, 0);

    const total = Math.abs(scope1) + Math.abs(scope2) + Math.abs(scope3);

    setDonutChartData({
      labels: ["Scope 1", "Scope 2", "Scope 3"],
      datasets: [
        {
          label: "GHG Emissions by Scope",
          data: [scope1, scope2, scope3].map((val) => Math.abs(val)),
          backgroundColor: ["#FF5733", "#33FF57", "#3357FF"],
          borderWidth: 1,
        },
      ],
    });

    setChangeChartData({
      labels: categories.map((cat) => cat.key),
      datasets: [
        {
          label: "Change in GHG Emissions",
          data: resultsData.map((res) => res.change),
          backgroundColor: ["#FF5733", "#33FF57", "#3357FF", "#FF8C00", "#FFC300", "#008000", "#6A5ACD"],
          borderWidth: 1,
        },
      ],
    });
  };

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" align="center" gutterBottom>
        GHG Emissions Comparison Analysis
      </Typography>
      <form>
        {/* Add form inputs for the different categories */}
        <button type="button" onClick={calculateComparison}>
          Compare Emissions
        </button>
      </form>
      <Grid container spacing={4}>
        <Grid item xs={12} md={6}>
          {donutChartData && <PieChart data={donutChartData} title="GHG Emissions by Scope" />}
        </Grid>
        <Grid item xs={12} md={6}>
          {changeChartData && <PieChart data={changeChartData} title="Change in GHG Emissions by Category" />}
        </Grid>
      </Grid>
      <Typography variant="h5" align="center" sx={{ mt: 4 }}>
        What are scopes?
      </Typography>
      <Grid container spacing={2}>
        <Grid item xs={12} md={4}>
          <InfoBox
            title="Scope 1"
            description="Direct emissions from owned or controlled sources."
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <InfoBox
            title="Scope 2"
            description="Indirect emissions from the generation of purchased energy."
          />
        </Grid>
        <Grid item xs={12} md={4}>
          <InfoBox
            title="Scope 3"
            description="All other indirect emissions that occur in a company's value chain."
          />
        </Grid>
      </Grid>
    </Box>
  );
};

export default GHGEmissionsComparison;
