import React, { useState } from "react";
import { Pie } from "react-chartjs-2";
import { Button, TextField, Typography, Box } from "@mui/material";
import Chart from "chart.js/auto"; // Importing chart.js

const GHGEmissionsComparison = () => {
  const [formData, setFormData] = useState({
    baselineFuel: "",
    currentFuel: "",
    baselineGas: "",
    currentGas: "",
    baselineElectricity: "",
    currentElectricity: "",
    baselineFlights: "",
    currentFlights: "",
    baselineWaste: "",
    currentWaste: "",
    baselineWater: "",
    currentWater: "",
    baselineWFH: "",
    currentWFH: "",
  });

  const [results, setResults] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const calculateComparison = () => {
    const categories = [
      { key: "Fuel", baseline: "baselineFuel", current: "currentFuel" },
      { key: "Gas", baseline: "baselineGas", current: "currentGas" },
      { key: "Electricity", baseline: "baselineElectricity", current: "currentElectricity" },
      { key: "Flights", baseline: "baselineFlights", current: "currentFlights" },
      { key: "Waste", baseline: "baselineWaste", current: "currentWaste" },
      { key: "Water", baseline: "baselineWater", current: "currentWater" },
      { key: "Work from Home", baseline: "baselineWFH", current: "currentWFH" },
    ];

    const resultsData = categories.map(({ key, baseline, current }) => {
      const baselineValue = parseFloat(formData[baseline]) || 0;
      const currentValue = parseFloat(formData[current]) || 0;
      const change = currentValue - baselineValue;
      const percentChange = baselineValue !== 0 ? ((change / baselineValue) * 100).toFixed(2) : "0.00";
      return { category: key, baseline: baselineValue, current: currentValue, change, percentChange };
    });

    setResults(resultsData);
  };

  // Pie chart data
  const pieChartData = {
    labels: results ? results.map((result) => result.category) : [],
    datasets: [
      {
        data: results ? results.map((result) => result.change) : [],
        backgroundColor: [
          "#FF6384", "#36A2EB", "#FFCE56", "#4BC0C0", "#FF9F40", "#9966FF", "#FFCD56",
        ], // Different colors for each section
      },
    ],
  };

  return (
    <div className="container">
      <Typography variant="h3" gutterBottom>Your GHG Emissions Comparison</Typography>

      <form id="ghgCalculator">
        <h3>Scope 1: Company Vehicles</h3>
        <TextField
          label="Baseline Year Fuel Consumption (liters)"
          type="number"
          id="baselineFuel"
          value={formData.baselineFuel}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Fuel Consumption (liters)"
          type="number"
          id="currentFuel"
          value={formData.currentFuel}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 1: Source of Heating (Natural Gas)</h3>
        <TextField
          label="Baseline Year Gas Usage (m³)"
          type="number"
          id="baselineGas"
          value={formData.baselineGas}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Gas Usage (m³)"
          type="number"
          id="currentGas"
          value={formData.currentGas}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 2: Electricity</h3>
        <TextField
          label="Baseline Year Electricity (kWh)"
          type="number"
          id="baselineElectricity"
          value={formData.baselineElectricity}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Electricity (kWh)"
          type="number"
          id="currentElectricity"
          value={formData.currentElectricity}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 3: Business Travel</h3>
        <TextField
          label="Baseline Year Flights (kg CO2e)"
          type="number"
          id="baselineFlights"
          value={formData.baselineFlights}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Flights (kg CO2e)"
          type="number"
          id="currentFlights"
          value={formData.currentFlights}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 3: Waste</h3>
        <TextField
          label="Baseline Year Waste (kg)"
          type="number"
          id="baselineWaste"
          value={formData.baselineWaste}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Waste (kg)"
          type="number"
          id="currentWaste"
          value={formData.currentWaste}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 3: Water Consumption</h3>
        <TextField
          label="Baseline Year Water Usage (liters)"
          type="number"
          id="baselineWater"
          value={formData.baselineWater}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Water Usage (liters)"
          type="number"
          id="currentWater"
          value={formData.currentWater}
          onChange={handleChange}
          fullWidth
        />

        <h3>Scope 3: Work from Home</h3>
        <TextField
          label="Baseline Year Work from Home Emissions (kg CO2e)"
          type="number"
          id="baselineWFH"
          value={formData.baselineWFH}
          onChange={handleChange}
          fullWidth
        />
        <TextField
          label="Current Year Work from Home Emissions (kg CO2e)"
          type="number"
          id="currentWFH"
          value={formData.currentWFH}
          onChange={handleChange}
          fullWidth
        />

        <Button variant="contained" color="primary" onClick={calculateComparison}>Compare Emissions</Button>
      </form>

      {results && (
        <div className="result">
          <Typography variant="h5" gutterBottom>Comparison Analysis Results</Typography>
          {results.map((result) => (
            <div key={result.category}>
              <strong>{result.category}:</strong>
              <ul>
                <li>Baseline: {result.baseline.toFixed(2)}</li>
                <li>Current: {result.current.toFixed(2)}</li>
                <li>Change: {result.change.toFixed(2)} ({result.percentChange}%)</li>
              </ul>
            </div>
          ))}
          <Box mt={4}>
            <Pie data={pieChartData} />
          </Box>
        </div>
      )}
    </div>
  );
};

export default GHGEmissionsComparison;
